    if (url) {
      setFile(null)
    }
  }, [url, setFile])

  return (
    <div className={classes.progressbarContainer}>
      <motion.div
        className={classes.progressbar}
        initial={{ width: 0 }}
        animate={{ width: `${progress}%` }}
      ></motion.div>
    </div>
  )
}

